/**
 * @type {Handler}
 */
export const handler = async (input) => {
const { width, height, profile, specs } = input;
const weightSpec = specs?.weight?.[profile];
if (!weightSpec) return { totalWeight: 0, error: 'Weight spec not found' };
const area = (width * height) / 1000000;
const totalWeight = area * weightSpec.weightPerM2;
return { totalWeight: Math.round(totalWeight * 100) / 100, area };
};
